import React, { useState, useEffect, useRef } from "react";

function ChatBox() {
  const [messages, setMessages] = useState([]);
  const [query, setQuery] = useState("");
  const [loading, setLoading] = useState(false);
  const [chats, setChats] = useState([]);
  const [activeChat, setActiveChat] = useState(null);
  const chatEndRef = useRef(null);

  // Auto-scroll
  const scrollToBottom = () => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => scrollToBottom(), [messages]);

  // Fetch saved chats
  useEffect(() => {
    fetchChats();
  }, []);

  const token = localStorage.getItem("token");

  const fetchChats = async () => {
    try {
      const res = await fetch("http://127.0.0.1:8000/saved-chats", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setChats(data.chats || []);
      if (!activeChat && data.chats.length > 0) {
        setActiveChat(data.chats[0]);
        setMessages(data.chats[0].messages || []);
      }
    } catch (error) {
      console.error("Error fetching chats:", error);
    }
  };

  const createNewChat = async () => {
    const title = prompt("Enter a title for this chat:", "New Chat");
    if (!title) return;
    try {
      const res = await fetch("http://127.0.0.1:8000/new-chat", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ title }),
      });
      const data = await res.json();
      if (data.chat_id) {
        await fetchChats();
        const newChat = { _id: data.chat_id, title, messages: [] };
        setActiveChat(newChat);
        setMessages([]);
      }
    } catch (error) {
      console.error("Error creating new chat:", error);
    }
  };

  const deleteChat = async (chatId) => {
    if (!window.confirm("Are you sure you want to delete this chat?")) return;
    try {
      await fetch(`http://127.0.0.1:8000/delete-chat/${chatId}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });
      if (activeChat && activeChat._id === chatId) {
        setMessages([]);
        setActiveChat(null);
      }
      fetchChats();
    } catch (error) {
      console.error("Error deleting chat:", error);
    }
  };

  const handleSend = async (e) => {
    e.preventDefault();
    if (!query.trim() || !activeChat) return;

    const userMsg = { role: "user", content: query };
    setMessages((prev) => [...prev, userMsg]);
    setQuery("");
    setLoading(true);

    try {
      const res = await fetch("http://127.0.0.1:8000/chat", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ chat_id: activeChat._id, query }),
      });
      const data = await res.json();

      if (!res.ok) throw new Error(data.detail || "Error");

      const botMsg = { role: "assistant", content: data.response };
      setMessages((prev) => [...prev, botMsg]);
      fetchChats();
    } catch (error) {
      console.error("Error sending message:", error);
      const errMsg = { role: "assistant", content: "‚ö†Ô∏è Error: " + error.message };
      setMessages((prev) => [...prev, errMsg]);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/";
  };

  const openChat = (chat) => {
    setActiveChat(chat);
    setMessages(chat.messages || []);
  };

  return (
    <div style={{ display: "flex", height: "100vh", backgroundColor: "#f3f4f6" }}>
      {/* Sidebar */}
      <div
        style={{
          width: "280px",
          backgroundColor: "#1f2937",
          color: "white",
          display: "flex",
          flexDirection: "column",
          padding: "15px",
        }}
      >
        <h2 style={{ textAlign: "center", marginBottom: "10px" }}>üí¨ My Chats</h2>

        <button
          onClick={createNewChat}
          style={{
            background: "#3b82f6",
            color: "white",
            border: "none",
            padding: "10px",
            borderRadius: "8px",
            cursor: "pointer",
            fontWeight: "bold",
            marginBottom: "10px",
          }}
        >
          ‚ûï New Chat
        </button>

        <div style={{ flex: 1, overflowY: "auto" }}>
          {chats.length === 0 ? (
            <p>No chats yet</p>
          ) : (
            chats.map((chat) => (
              <div
                key={chat._id}
                style={{
                  background:
                    activeChat && chat._id === activeChat._id
                      ? "#374151"
                      : "#2d3748",
                  padding: "10px",
                  marginBottom: "8px",
                  borderRadius: "8px",
                  cursor: "pointer",
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                }}
              >
                <div onClick={() => openChat(chat)}>
                  <p style={{ margin: 0, fontSize: "14px", color: "#e5e7eb" }}>
                    {chat.title.length > 25
                      ? chat.title.slice(0, 25) + "..."
                      : chat.title}
                  </p>
                </div>
                <button
                  onClick={() => deleteChat(chat._id)}
                  style={{
                    background: "none",
                    border: "none",
                    color: "#ef4444",
                    cursor: "pointer",
                    fontSize: "16px",
                  }}
                >
                  üóëÔ∏è
                </button>
              </div>
            ))
          )}
        </div>

        <button
          onClick={handleLogout}
          style={{
            marginTop: "10px",
            background: "#ef4444",
            border: "none",
            padding: "10px",
            borderRadius: "8px",
            color: "white",
            cursor: "pointer",
            fontWeight: "bold",
          }}
        >
          Logout
        </button>
      </div>

      {/* Chat Area */}
      <div
        style={{
          flex: 1,
          display: "flex",
          flexDirection: "column",
          backgroundColor: "white",
          borderLeft: "1px solid #ccc",
        }}
      >
        {/* Chat Header */}
        <div
          style={{
            padding: "15px 20px",
            borderBottom: "1px solid #ccc",
            fontWeight: "bold",
            backgroundColor: "#f9fafb",
          }}
        >
          {activeChat ? activeChat.title : "Select or Create a Chat"}
        </div>

        {/* Messages */}
        <div style={{ flex: 1, padding: "20px", overflowY: "auto" }}>
          {messages.map((msg, idx) => (
            <div
              key={idx}
              style={{
                display: "flex",
                justifyContent:
                  msg.role === "user" ? "flex-end" : "flex-start",
                marginBottom: "10px",
              }}
            >
              <div
                style={{
                  backgroundColor:
                    msg.role === "user" ? "#3b82f6" : "#e5e7eb",
                  color: msg.role === "user" ? "#fff" : "#000",
                  padding: "10px 15px",
                  borderRadius: "12px",
                  maxWidth: "70%",
                  whiteSpace: "pre-wrap",
                }}
              >
                {msg.content}
              </div>
            </div>
          ))}
          <div ref={chatEndRef} />
        </div>

        {/* Input Box */}
        {activeChat && (
          <form
            onSubmit={handleSend}
            style={{
              display: "flex",
              borderTop: "1px solid #ccc",
              padding: "10px",
              background: "#fafafa",
            }}
          >
            <input
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="Type your message..."
              style={{
                flex: 1,
                padding: "12px",
                borderRadius: "12px",
                border: "1px solid #ccc",
                marginRight: "10px",
              }}
            />
            <button
              type="submit"
              disabled={loading}
              style={{
                padding: "12px 20px",
                borderRadius: "12px",
                border: "none",
                backgroundColor: "#3b82f6",
                color: "#fff",
                fontWeight: "bold",
                cursor: "pointer",
              }}
            >
              {loading ? "Sending..." : "Send"}
            </button>
          </form>
        )}
      </div>
    </div>
  );
}

export default ChatBox;
