// src/pages/LoginSignup.js
import React, { useState } from "react";
import { login, signup } from "../api";

const LoginSignup = () => {
  const [isLogin, setIsLogin] = useState(false); // toggle login/signup
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      const res = await signup({ email, password });
      setMessage("Signup successful! You can now login.");
      setIsLogin(true); // switch to login after signup
    } catch (err) {
      setMessage(err.response?.data?.detail || "Signup failed");
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await login({ email, password });
      localStorage.setItem("token", res.token); // save JWT
      setMessage("Login successful!");
      window.location.href = "/chat"; // redirect to chat
    } catch (err) {
      setMessage(err.response?.data?.detail || "Login failed");
    }
  };

  return (
    <div style={{ maxWidth: "400px", margin: "auto", padding: "20px" }}>
      <h2>Signup</h2>
      <form onSubmit={handleSignup}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Signup</button>
      </form>

      <h2 style={{ marginTop: "30px" }}>Login</h2>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>

      {message && <p style={{ marginTop: "20px", color: "red" }}>{message}</p>}
    </div>
  );
};

export default LoginSignup;
