import React, { useState } from "react";
import api from "../api"; // default import

function ChatBox() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  const handleSend = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { sender: "You", text: input }];
    setMessages(newMessages);

    try {
      const response = await api.sendMessage({ query: input }); // use api.sendMessage
      setMessages((prev) => [...prev, { sender: "Bot", text: response.response }]);
    } catch (err) {
      setMessages((prev) => [...prev, { sender: "Bot", text: "Error sending message" }]);
      console.error("Error sending message:", err);
    }

    setInput("");
  };

  return (
    <div>
      <div>
        {messages.map((m, i) => (
          <div key={i}><strong>{m.sender}:</strong> {m.text}</div>
        ))}
      </div>
      <input value={input} onChange={(e) => setInput(e.target.value)} />
      <button onClick={handleSend}>Send</button>
    </div>
  );
}

export default ChatBox;
