import React, { useEffect, useState } from "react";
import api from "../api"; // default import

function SavedAnswers() {
  const [answers, setAnswers] = useState([]);

  useEffect(() => {
    const fetchAnswers = async () => {
      try {
        const data = await api.getSavedAnswers(); // use api.getSavedAnswers
        setAnswers(data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchAnswers();
  }, []);

  return (
    <div>
      <h2>Saved Answers</h2>
      {answers.map((a) => (
        <div key={a._id}>
          <p><strong>You:</strong> {a.query}</p>
          <p><strong>Bot:</strong> {a.response}</p>
          <hr />
        </div>
      ))}
    </div>
  );
}

export default SavedAnswers;
