import React, { useState, useEffect, useRef } from "react";

function ChatBox() {
  const [messages, setMessages] = useState([]);
  const [query, setQuery] = useState("");
  const [loading, setLoading] = useState(false);
  const [savedChats, setSavedChats] = useState([]);
  const chatEndRef = useRef(null);

  // Scroll to bottom when new messages arrive
  const scrollToBottom = () => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };
  useEffect(() => scrollToBottom(), [messages]);

  // Fetch saved chats on mount
  useEffect(() => {
    fetchSavedChats();
  }, []);

  const fetchSavedChats = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await fetch("http://127.0.0.1:8000/saved-chats", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setSavedChats(data.chats || []);
    } catch (error) {
      console.error("Error fetching chats:", error);
    }
  };

  const handleSend = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;

    const userMessage = { sender: "You", text: query };
    setMessages((prev) => [...prev, userMessage]);
    setQuery("");
    setLoading(true);

    try {
      const token = localStorage.getItem("token");
      const res = await fetch("http://127.0.0.1:8000/chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ query: userMessage.text }),
      });

      const data = await res.json();
      const botMessage = { sender: "Bot", text: data.response || "Error" };
      setMessages((prev) => [...prev, botMessage]);
      fetchSavedChats(); // refresh sidebar
    } catch (error) {
      console.error("Error sending message:", error);
      const botMessage = { sender: "Bot", text: "Error sending message" };
      setMessages((prev) => [...prev, botMessage]);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/";
  };

  const loadPreviousChat = (chat) => {
    setMessages([
      { sender: "You", text: chat.query },
      { sender: "Bot", text: chat.response },
    ]);
  };

  return (
    <div style={{ display: "flex", height: "100vh", background: "#f0f0f0" }}>
      {/* Sidebar */}
      <div
        style={{
          width: "250px",
          background: "#1e1e2f",
          color: "white",
          padding: "20px",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <h3 style={{ textAlign: "center", marginBottom: "20px" }}>ðŸ’¬ Chats</h3>
        <div
          style={{
            flex: 1,
            overflowY: "auto",
          }}
        >
          {savedChats.length === 0 ? (
            <p>No saved chats yet</p>
          ) : (
            savedChats.map((chat, idx) => (
              <div
                key={idx}
                style={{
                  background: "#2d2d40",
                  marginBottom: "10px",
                  padding: "10px",
                  borderRadius: "8px",
                  cursor: "pointer",
                }}
                onClick={() => loadPreviousChat(chat)}
              >
                <p style={{ margin: 0, color: "#ddd", fontSize: "14px" }}>
                  {chat.query.slice(0, 40)}...
                </p>
              </div>
            ))
          )}
        </div>
        <button
          onClick={handleLogout}
          style={{
            background: "#ff4444",
            border: "none",
            padding: "10px",
            color: "white",
            borderRadius: "8px",
            cursor: "pointer",
            fontWeight: "bold",
          }}
        >
          Logout
        </button>
      </div>

      {/* Chat Area */}
      <div
        style={{
          flex: 1,
          display: "flex",
          flexDirection: "column",
          background: "#fff",
          borderLeft: "1px solid #ccc",
        }}
      >
        <div style={{ flex: 1, padding: "20px", overflowY: "auto" }}>
          {messages.map((msg, idx) => (
            <div
              key={idx}
              style={{
                display: "flex",
                justifyContent: msg.sender === "You" ? "flex-end" : "flex-start",
                marginBottom: "10px",
              }}
            >
              <div
                style={{
                  backgroundColor:
                    msg.sender === "You" ? "#007bff" : "#e5e5ea",
                  color: msg.sender === "You" ? "#fff" : "#000",
                  padding: "10px 15px",
                  borderRadius: "15px",
                  maxWidth: "70%",
                }}
              >
                {msg.text}
              </div>
            </div>
          ))}
          <div ref={chatEndRef} />
        </div>

        <form
          onSubmit={handleSend}
          style={{
            display: "flex",
            borderTop: "1px solid #ccc",
            padding: "10px",
            background: "#fafafa",
          }}
        >
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Type your message..."
            style={{
              flex: 1,
              padding: "12px",
              borderRadius: "12px",
              border: "1px solid #ccc",
              marginRight: "10px",
            }}
          />
          <button
            type="submit"
            disabled={loading}
            style={{
              padding: "12px 20px",
              borderRadius: "12px",
              border: "none",
              backgroundColor: "#007bff",
              color: "#fff",
              fontWeight: "bold",
              cursor: "pointer",
            }}
          >
            {loading ? "Sending..." : "Send"}
          </button>
        </form>
      </div>
    </div>
  );
}

export default ChatBox;
