import axios from "axios";

// ---------------- BASE CONFIG ----------------
axios.defaults.baseURL = "http://127.0.0.1:8000";

// Add token to headers if present
axios.interceptors.request.use((config) => {
  const token = localStorage.getItem("token");
  if (token) {
    config.headers["Authorization"] = `Bearer ${token}`;
  }
  return config;
});

// ---------------- AUTH ----------------
async function login(data) {
  try {
    const response = await axios.post("/login", data);
    return response.data;
  } catch (err) {
    console.error("Login error:", err);
    throw err.response?.data || { detail: "Login failed" };
  }
}

async function signup(data) {
  try {
    const response = await axios.post("/signup", data);
    return response.data;
  } catch (err) {
    console.error("Signup error:", err);
    throw err.response?.data || { detail: "Signup failed" };
  }
}

// ---------------- CHAT ----------------
async function sendMessage(data) {
  try {
    const response = await axios.post("/chat", data);
    return response.data;
  } catch (err) {
    console.error("Error sending message:", err);
    return { error: err.response?.data?.detail || "Error sending message" };
  }
}

// ---------------- SAVED ANSWERS ----------------
async function getSavedAnswers() {
  try {
    const response = await axios.get("/saved-answers");
    return response.data;
  } catch (err) {
    console.error("Error fetching saved answers:", err);
    return { error: err.response?.data?.detail || "Error fetching saved answers" };
  }
}

// ---------------- EXPORT ----------------
const api = {
  login,
  signup,
  sendMessage,
  getSavedAnswers,
};

export default api;
