import React, { useState, useEffect, useRef } from "react";

function ChatBox() {
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [messages, setMessages] = useState([]);
  const [query, setQuery] = useState("");
  const [imageFile, setImageFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  const scrollToBottom = () => chatEndRef.current?.scrollIntoView({ behavior: "smooth" });

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const token = localStorage.getItem("token");

  const loadChats = async () => {
    const res = await fetch("http://127.0.0.1:8000/chats", {
      headers: { Authorization: `Bearer ${token}` },
    });
    const data = await res.json();
    setChats(data.chats || []);
  };

  useEffect(() => {
    loadChats();
  }, []);

  const newChat = async () => {
    const res = await fetch("http://127.0.0.1:8000/new-chat", {
      method: "POST",
      headers: { Authorization: `Bearer ${token}` },
    });
    const data = await res.json();
    setChats((prev) => [...prev, data]);
  };

  const deleteChat = async (id) => {
    await fetch(`http://127.0.0.1:8000/chat/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });
    setChats((prev) => prev.filter((c) => c._id !== id));
    if (selectedChat === id) {
      setSelectedChat(null);
      setMessages([]);
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    window.location.href = "/";
  };

  const handleSend = async (e) => {
    e.preventDefault();
    if (!query.trim() && !imageFile) return;
    if (!selectedChat) return alert("Please select a chat first.");

    const userMsg = {
      sender: "You",
      text: imageFile ? `[Image: ${imageFile.name}] ${query}` : query,
    };
    setMessages((prev) => [...prev, userMsg]);
    setQuery("");
    setLoading(true);

    try {
      let data;
      if (imageFile) {
        const formData = new FormData();
        formData.append("file", imageFile);
        formData.append("prompt", query || "Describe this image.");
        formData.append("chat_id", selectedChat);

        const res = await fetch("http://127.0.0.1:8000/analyze-image", {
          method: "POST",
          headers: { Authorization: `Bearer ${token}` },
          body: formData,
        });
        data = await res.json();
        setImageFile(null);
      } else {
        const res = await fetch("http://127.0.0.1:8000/chat", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ query, chat_id: selectedChat }),
        });
        data = await res.json();
      }

      const botMsg = { sender: "Bot", text: data.response };
      setMessages((prev) => [...prev, botMsg]);
    } catch {
      setMessages((prev) => [...prev, { sender: "Bot", text: "‚ùå Error" }]);
    } finally {
      setLoading(false);
    }
  };

  const handleCopy = (text) => {
    navigator.clipboard.writeText(text);
    alert("‚úÖ Copied to clipboard!");
  };

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      {/* Sidebar */}
      <div
        style={{
          width: "250px",
          backgroundColor: "#1f2937",
          color: "#fff",
          display: "flex",
          flexDirection: "column",
          padding: "10px",
        }}
      >
        <h2 style={{ margin: "10px 0", fontSize: "18px", textAlign: "center" }}>üí¨ My Chats</h2>
        <button
          onClick={newChat}
          style={{
            backgroundColor: "#3b82f6",
            border: "none",
            color: "#fff",
            padding: "8px",
            borderRadius: "8px",
            marginBottom: "10px",
            cursor: "pointer",
          }}
        >
          + New Chat
        </button>

        <div style={{ flex: 1, overflowY: "auto" }}>
          {chats.map((chat) => (
            <div
              key={chat._id}
              style={{
                backgroundColor: selectedChat === chat._id ? "#374151" : "#2d3748",
                margin: "5px 0",
                padding: "8px",
                borderRadius: "6px",
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                cursor: "pointer",
              }}
              onClick={() => setSelectedChat(chat._id)}
            >
              <span>{chat.title}</span>
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  deleteChat(chat._id);
                }}
                style={{
                  background: "none",
                  border: "none",
                  color: "#f87171",
                  cursor: "pointer",
                }}
              >
                üóëÔ∏è
              </button>
            </div>
          ))}
        </div>

        <button
          onClick={logout}
          style={{
            backgroundColor: "#ef4444",
            color: "#fff",
            padding: "8px",
            borderRadius: "8px",
            border: "none",
            cursor: "pointer",
            marginTop: "10px",
          }}
        >
          Logout
        </button>
      </div>

      {/* Chat Area */}
      <div style={{ flex: 1, display: "flex", flexDirection: "column", backgroundColor: "#f3f4f6" }}>
        <div style={{ flex: 1, padding: "20px", overflowY: "auto" }}>
          {selectedChat ? (
            messages.map((msg, i) => (
              <div
                key={i}
                style={{
                  display: "flex",
                  flexDirection: "column",
                  alignItems: msg.sender === "You" ? "flex-end" : "flex-start",
                  marginBottom: "10px",
                }}
              >
                <div
                  style={{
                    backgroundColor: msg.sender === "You" ? "#3b82f6" : "#e5e7eb",
                    color: msg.sender === "You" ? "#fff" : "#000",
                    padding: "10px",
                    borderRadius: "12px",
                    maxWidth: "70%",
                  }}
                >
                  {msg.text}
                </div>
                {msg.sender === "Bot" && (
                  <button
                    onClick={() => handleCopy(msg.text)}
                    style={{
                      fontSize: "12px",
                      background: "none",
                      border: "none",
                      color: "#2563eb",
                      cursor: "pointer",
                    }}
                  >
                    üìã Copy
                  </button>
                )}
              </div>
            ))
          ) : (
            <p style={{ textAlign: "center", color: "#555" }}>Select or create a chat</p>
          )}
          <div ref={chatEndRef} />
        </div>

        {imageFile && (
          <div style={{ margin: "10px", textAlign: "center" }}>
            <p>üñºÔ∏è {imageFile.name}</p>
            <img
              src={URL.createObjectURL(imageFile)}
              alt="preview"
              style={{ maxWidth: "200px", borderRadius: "8px" }}
            />
          </div>
        )}

        <form
          onSubmit={handleSend}
          style={{
            display: "flex",
            alignItems: "center",
            padding: "10px",
            borderTop: "1px solid #ccc",
            backgroundColor: "#fff",
          }}
        >
          <input type="file" accept="image/*" onChange={(e) => setImageFile(e.target.files[0])} />
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Type a message..."
            style={{
              flex: 1,
              padding: "10px",
              margin: "0 10px",
              borderRadius: "8px",
              border: "1px solid #ccc",
            }}
          />
          <button
            type="submit"
            disabled={loading}
            style={{
              padding: "10px 15px",
              border: "none",
              backgroundColor: "#3b82f6",
              color: "#fff",
              borderRadius: "8px",
              cursor: "pointer",
            }}
          >
            {loading ? "Sending..." : "Send"}
          </button>
        </form>
      </div>
    </div>
  );
}

export default ChatBox;
